vec3 palette(float l, float h) { return l + l * vec3(.7874, .2848, .9278) * cos(h - vec3(0, 2.1, 4.2)); }


void mainImage(out vec4 pixel, vec2 uv) {
    uv = (uv + uv - iResolution.xy) / iResolution.x;
    float hue = atan(uv.y, uv.x) + iTime,
          lightness = pow(smoothstep(2., 0., length(uv) - .1), 9.);

    // pastel
    vec3 color = palette(lightness, hue);

    color = mix(color, vec3(1), pow(lightness, 9.));
    pixel.rgb = pow(color, vec3(1) / 2.2);
}
