mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=cos(iTime);

    p=vec3(log2(length(p)), p.z/2e4, atan(p.y,p.z));
    p.xy*=rotate2D(iTime);

    for(j=0.;j++<10.;)
        p=abs(abs(p)-1.4)-.01,
        d=max(min(p.z,p.y),p.x),
        d-=dot(p,p)/2e4;
    e+=d;
}o+=vec4(-log(p/4.),1)*3./i;
}
