uint binary_gcd(uvec2 v) // https://www.shadertoy.com/view/mltyRM binary gcd
{
    if (v.x == 0u) return v.y;
    if (v.y == 0u) return v.x;
    uint k;
    for(k = 0u; ((v.x | v.y) & 1u) == 0u; ++k) v >>= 1u;
    while((v.x & 1u) == 0u) v.x >>= 1u;
    do
    {
        while((v.y & 1u) == 0u) v.y >>= 1u;
        if (v.x > v.y) v = v.yx;
        v.y -= v.x;
    }
    while (v.y != 0u);
    return v.x << k;
}

uint gcd(uvec2 v) // NOTE: modulo-based gcd is way more compact...
{
    while(v.y != 0u) v = uvec2(v.y, v.x % v.y);
    return v.x;
}

bool checker(uvec2 p)
{
    return (p.x&1u)!=(p.y&1u);
}

// https://www.shadertoy.com/view/WttXWX "Best" Integer Hash by FabriceNeyret2,
// implementing Chris Wellons https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x)
{
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
    return x;
}

#define HASH(u) triple32(u)
//#define HASH(u) lowbias32(u)

uint uhash(uvec2 v)
{
//return uvec2(0u); // verify grid alignment
    return HASH((uint(v.x) + HASH(uint(v.y))));
}

// https://www.shadertoy.com/view/DtjyWD integer division - rounding down
ivec2 div_floor(ivec2 a, int b) // vector version thanks to Fabrice
{
    ivec2  S = (sign(abs(a*b))-sign(a*b))/2; // 0 if a*b >= 0
    return S * ((1 - abs(a)) / abs(b) - 1)+(1-S)*(a / b); // emulates ()?:
}

// this implementation avoids operator % for stability on windows XXX differs from original at 0 ? XXX
ivec2 mod_positive(ivec2 a, int b)
{
    return a - div_floor(a, b) * b;
}

// resolution of underlying grid
#define RES 8

struct block
{
    ivec4 minmax;
    ivec2 id;
};

int block_hash(ivec2 tile, int x, int y, bool choose, bool mirror)
{
    ivec2 d = (mirror?ivec2(y, x):ivec2(x, y));
    return int(uhash(uvec2(tile+d)*2u+uint(choose)) % uint(RES))+(choose?d.y:d.x)*RES;
    //ivec2 r = ivec2(uhash(uvec2(tile+d)*2u+uint(choose)) % uint(RES))+d*RES;
    //return choose?r.y:r.x;
}

// Inspired by https://www.shadertoy.com/view/Ws3GRs Asymmetric Blocks by Shane,
// and also by https://www.shadertoy.com/view/tsVGRz Oddtiles (optimized) by stb.
// Checkerboard tiling of alternating (horizontal vs. vertical) (double-T-) crossings.
// Returns minimum, maximum, id.
// Severely compactified/crunched version.
block blocks_layout(ivec2 p) // https://www.shadertoy.com/view/mdcBR2 asymmetric blocks compactified by jt
{
    ivec2 tile = div_floor(p, RES); // global coordinates of tile
    ivec2 local = p - tile * RES;

    //bool flip = (tile.x % 2) != (tile.y % 2); // XXX breaks on windows XXX
    bool flip = (tile.x & 1) != (tile.y & 1); // true: vertical line goes through, false: horizontal line goes through
    //flip = !flip; // "dual" pattern (i.e. T-crossings flipped)

    // | identity
    // NW N NE
    //  W C E
    // SW S SE

    // - transposed (NOTE: y-coordinate up but matrix y-index down!)
    // SE W NE
    //  N C S
    // SW E NW

    if(flip) // horizontal line goes through: swap vertical with horizontal
    {
        local = local.yx;
    }

    // vertical line goes through (on flip transpose for horizontal line)

    //                 c
    //   +-+---------+---+-------+
    //   | |         |   |       |
    //   | |         |   #-------+ d
    // d +-#-#-------+---M       |
    //   |   |       |   |       |
    //   +---+-------+---+-------+
    //   |   |       |   |       |
    //   |   m-------+---#---#---+
    //   +---#       |       |   |
    //   |   |       |       |   |
    //   +---+-------+-------+---+

    int c = block_hash(tile, 0, 0, flip, flip); // offset of central line

    int x = 1-int(local.x < c); // [0,1]
    int X = 2*x-1; // [-1,+1]

    int d = block_hash(tile, X, 0,!flip, flip); // offset of branching line

    int y = 1-int(local.y < d); // [0,1]
    int Y = 2*y-1; // [-1,+1]

    ivec4 minmax =
        ivec4
        (
            // block corner in (0,0) tile
            c,
            d,
            // block corner in (X,Y) tile
            block_hash(tile,X,Y, flip, flip),
            block_hash(tile,0,Y,!flip, flip)
        );

    if(flip) tile = tile.yx;

    minmax += tile.xyxy * RES;

    ivec2 id = tile + ivec2(x,y);

    if(flip) // horizontal line goes through: unswap vertical with horizontal
    {
        minmax = minmax.yxwz;
        id = id.yx;
    }

    // fix order of min/max corners NOTE: defer division by 2 because center & extend could be half-integers!
    ivec2 center2 = minmax.xy+minmax.zw;
    ivec2 extent2 = abs(minmax.xy-minmax.zw);

    minmax.xy = (center2-extent2)/2;
    minmax.zw = (center2+extent2)/2;

    return block(minmax, id);
}

vec3 hash32(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

#define scale (float(RES*3))

void mainImage(out vec4 fragColor, in vec2 p)
{
    vec2 R = iResolution.xy;
    p = (2.0 * p - R) / R.y;
    vec2 m = iMouse.xy;
    m = (2.0 * m - R) / R.y;

    ivec2 i = ivec2(floor(p*scale));
    block b = blocks_layout(i);
    uvec2 size = uvec2(b.minmax.zw-b.minmax.xy);
    uint cd = binary_gcd(size);

    vec3 color = hash32(vec2(b.id));
    if(((b.id.x^b.id.y)&1)==0)
        color = 1.0 - color;
    //if(all(greaterThan(i, ivec2(b.minmax.xy)))) color *= 0.0;
    // use GCD to neatly subdivide integer rectangles into squares
    color *= checker(uvec2(i - b.minmax.xy)/cd) ? 1.0 : 0.5;
    // differences between pixel-position and corners
    vec2 q0 = p-vec2(b.minmax.xy)/scale;
    vec2 q1 = p-vec2(b.minmax.zw)/scale;
    // visualize edges
    color = min(color, vec3(smoothstep(0.005, 0.01,+q0.x)));
    color = min(color, vec3(smoothstep(0.005, 0.01,+q0.y)));
    color = min(color, vec3(smoothstep(0.005, 0.01,-q1.x)));
    color = min(color, vec3(smoothstep(0.005, 0.01,-q1.y)));
    // visualize min-max
    //color = min(color, vec3(smoothstep(0.005, 0.01, abs(length(q0)-0.05))));
    //color = min(color, vec3(smoothstep(0.005, 0.01, abs(length(q1)-0.05))));

    //vec3 color = vec3((b.id.x^b.id.y)&1);
    if(iMouse.z > 0.0)
    {
        vec2 r = p*scale/2.0;
        float pi = 3.1415926;
        //color *= mix(0.5, 1.0, step(0.0, sin(r.x*pi*2.0)*sin(r.y*pi*2.0)));
        color *= step(0.1, min(color, vec3(min(fract(p.x*scale), fract(p.y*scale)))));
        vec2 q = fract(p*scale/float(RES));
        q = step(0.01, q);
        color = max(color, vec3(1.0-min(q.x, q.y)));
    }

    color = sqrt(color);
    fragColor = vec4(color, 1.0);
}
