void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 tint = vec4(0.85);
    float sin_pos = float(iTime * 20.0);

    float sin_offset_1 = sin(sin_pos + uv.y*50.0)*0.0005;
    float sin_offset_2 = sin(sin_pos + uv.y*30.0)*0.0009;
    float sin_offset_3 = sin(sin_pos + uv.x*60.0)*0.0001;
    float round_offset = sin(uv.x*3.14159)*0.03;
    vec4 color = texture(iChannel0, uv+vec2(sin_offset_1, -round_offset));
    color.r = texture(iChannel0, uv+vec2(sin_offset_2, -round_offset)).r;
    color *= tint;
    float crt_wave_val = sin((uv.y + sin_offset_3 - round_offset)*400.0);
    crt_wave_val *= crt_wave_val;
    color.r += crt_wave_val*(1.0/16.0);
    color.g += crt_wave_val*(1.0/10.0);
    color.b += crt_wave_val*(1.0/6.0);

    fragColor = color;
}
