 #define TAU 6.2831853071795864769252867665590

 float fill(float x, float size) {
    return 1.-step(size, x);
 }
 float stroke(float x, float size, float w) {
    // float d = step(size, x+w*.5) - step(size, x-w*.5);
    // return clamp(d, 0., 1.);
    return step(abs(x-size), w*.5);
 }
 vec2 rotate(vec2 st, float a) {
    st = mat2(cos(a),-sin(a),
              sin(a),cos(a))*(st-.5);
    return st+.5;
 }

 float circleSDF(vec2 st) {
    return length(st-.5)*2.;
 }

 float starSDF(vec2 st, int V, float s) {
    st = st*4.-2.;
    float a = atan(st.y, st.x)/TAU;
    float seg = a * float(V);
    a = ((floor(seg) + 0.5)/float(V) + 
        mix(s,-s,step(.5,fract(seg)))) 
        * TAU;
    return abs(dot(vec2(cos(a),sin(a)),
                   st));
 }

 float flowerSDF(vec2 st, int N) {
    st = st*2.-1.;
    float r = length(st)*2.;
    float a = atan(st.y,st.x);
    float v = float(N)*.5;
    return 1.-(abs(cos(a*v))*.5+.5)/r;
 }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    // vec2 st = ( fragCoord - .5*iResolution.xy ) / min( iResolution.x, iResolution.y );
    vec3 color = vec3(0.);

    st = (st-.5)*1.1912+.5;
    if (iResolution.y > iResolution.x ) {
        st.y *= iResolution.y/iResolution.x;
        st.y -= (iResolution.y*.5-iResolution.x*.5)/iResolution.x;
    } else {
        st.x *= iResolution.x/iResolution.y;
        st.x -= (iResolution.x*.5-iResolution.y*.5)/iResolution.y;
    }

    color.x += fill(flowerSDF(rotate(st,fract(iTime * 0.01) * 6.28),5),fract(iTime * 0.08) * 0.5);
    color -= step(.95,starSDF(rotate(st,0.628 + fract(iTime * 0.01) * 6.28),5,.1));
    color = clamp(color,0.3,1.);
    float circle = circleSDF(st);
    color.z += stroke(circle,.1,.15);
    color.y += stroke(circle,.8,.07);

    // Output to screen
    fragColor = vec4(color,1.0);
}
