renderpass.code:
#define res iResolution.xy
#define ss(a) smoothstep(-1./res.y, 1./res.y, a)

#define tanh2(a) ((exp2(a)-1.)/(exp2(a)+1.))
#define atanh2(a) log2((1.+a)/(1.-(a)))

// Cheapest, soft square wave, use only with high a (>8 or so), 
// or if you don't mind not oscillating to +-1
#define thc0(a, x) tanh2(a*cos(x))

// More correct wave, hits +-1
// a~=0 is cos (but breaks at a=0)
// a>0 is soft square wave
#define thc1(a, x) tanh2(a*cos(x))/tanh2(a)

// Probably not worth using this over thc1, it's about 2x more 
// expensive and very similar (unless you want to vary between tri, square)
// Same as thc3 but can't change pulse width
#define thc2(a, x) tanh2((a)*atanh2(cos(x)))

// Has pulse wave, great control, is expensive (not much more than thc2)
// a>0 controls sharpness 
// o in [-1,1] controls pulse width
// a=.6, o=0 is "triangle", 
// a=1,  o=0 is cos, 
// a>1,  o=0 is soft square
// a>1,  o!=0 is soft pulse
#define thc3(a, o, x) tanh2((a)*(atanh2(cos(x))-atanh2(o)))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * res) / res.y;    
    float t = iTime;

    vec2 pSqu1  = 0.5 * vec2(thc1(8., t),        0.1);
    vec2 pSqu2  = 0.5 * vec2(thc2(2.8, t),       0.0);
    vec2 pPulse = 0.5 * vec2(thc3(2.8, 0.5, t), -0.1);
    vec2 pTri   = 0.5 * vec2(thc3(0.6, 0., t),  -0.2);

    float sSqu1  = ss(0.02 - length(uv - pSqu1));
    float sSqu2  = ss(0.02 - length(uv - pSqu2));
    float sPulse = ss(0.02 - length(uv - pPulse));
    float sTri   = ss(0.02 - length(uv - pTri)); 

    vec3 col = vec3(0);
    col = mix(col, vec3(1,.5,.5), sSqu1);
    col = mix(col, vec3(.5,1,.5), sSqu2);
    col = mix(col, vec3(.5,.5,1), sPulse);
    col = mix(col, vec3(.9),      sTri);

    fragColor = vec4(col, 1);
}
