bool IsInCyrcle(vec2 uv, float r, vec2 offset) {
  float x = uv.x - 0.5 - offset.x;
  float y = uv.y - 0.5 - offset.y;

  float d = length(vec2(x, y)) - r;

  if(d>0.) return false; else return true;
}

float cross2d(vec2 a, vec2 b) {
    return a.x * b.y - a.y * b.x;
}

bool isPointInTriangle(vec2 p, vec2 vertex1, vec2 vertex2, vec2 vertex3) {

    vec2 e0 = vertex2 - vertex1;
    vec2 e1 = vertex3 - vertex2;
    vec2 e2 = vertex1 - vertex3;

    vec2 v0 = p - vertex1;
    vec2 v1 = p - vertex2;
    vec2 v2 = p - vertex3;

    float sign1 = cross2d(e0, v0);
    float sign2 = cross2d(e1, v1);
    float sign3 = cross2d(e2, v2);

    return (sign1 < 0.0 && sign2 < 0.0 && sign3 < 0.0);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = fragCoord / iResolution.xy;
  uv.x *= iResolution.x / iResolution.y;

  vec2 offset_cyrcle = vec2(sin(iTime*2.)*0.2, cos(iTime*2.)*0.2);

  vec2 offset = vec2(mod(iTime * 1.0, 1.0) * iResolution.x / iResolution.y, mod(iTime * 1.0, 1.0)); 

  vec2 vertex1 = vec2(0.3+offset.x, 0.3-offset.y);
  vec2 vertex2 = vec2(0.1+offset.x, 0.2-offset.y);
  vec2 vertex3 = vec2(0.5+offset.x, 0.9-offset.y);

  bool isInCircle = IsInCyrcle(uv, .3, offset_cyrcle);
  bool isInTriangle1 = isPointInTriangle(uv, vertex1, vertex2, vertex3);
  bool isInTriangle2 = isPointInTriangle(uv, vec2(0.1, 0.1), vec2(0.1, 0.4), vec2(0.4, 0.1));

  if (isInCircle) {
    fragColor = vec4(1.0, 0.0, 0.0, 1.0); 
  } else if (isInTriangle1) {
    fragColor = vec4(0.0, 0.0, 1.0, 1.0); 
  } else if (isInTriangle2){
    fragColor = vec4(0.0, 1.0, 0.0,1.0); 
  } else fragColor = vec4(0.0, 0.0, 0.0, 1.0);
}
