vec2 zexp(vec2 A)
{
    return vec2(pow(2.719, A.x)*cos(A.y), pow(2.719, A.x)*sin(A.y));
}
vec2 zlog(vec2 A)
{
    return vec2(log(A.x*A.x+A.y*A.y)/log(2.719)/2., atan(A.y, A.x));
}
vec2 mult(vec2 A, vec2 B)
{
    return vec2(A.x*B.x-A.y*B.y, A.x*B.y+A.y*B.x);
}
vec2 zinvert(vec2 A)
{
    return vec2(1./sqrt(A.x*A.x+A.y*A.y)*cos(-atan(A.y, A.x)), 1./sqrt(A.x*A.x+A.y*A.y)*sin(-atan(A.y, A.x)));
}
vec2 div(vec2 A, vec2 B)
{
    return mult(A, zinvert(B));
}
vec2 zpow(vec2 A, vec2 B)
{
    return zexp(mult(zlog(A), B));
}
float zabs(vec2 A)
{
    return sqrt(A.x*A.x+A.y*A.y);
}
vec2 fib(vec2 A)
{
    return (zpow(vec2(1.6180339, 0.0), A) - zpow(vec2(-0.6180339, 0.0), A))/sqrt(5.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 1.0/pow(10.0, (iTime/4.0)-1.2);
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    uv *= scale;
    float maxi = 100.;
    float i = 0.0;
    while(i < maxi && zabs(uv)<50.0){
        uv = fib(uv);
        i = i+1.;
    }
    if(i < maxi){
        float noose = log(zabs(uv));
        i = i - (log(noose/log(2.0))/log(2.0)) + 1.0;
    }
    fragColor = vec4(cos(pow((1.0/log(abs(uv.y)))/log(abs(uv.x)), 0.25)-1.5), cos(pow((1.0/log(abs(uv.y)))/log(abs(uv.x)), 0.08)+10.0)/2.0, cos(pow((1.0/log(abs(uv.y)))/log(abs(uv.x)), 0.003)+0.5), 0.0);
    if(zabs(uv) < 5.0){
        fragColor = vec4(uv.x);
    }
}
