bool isBlackSource(int row, int col) {
    return row % 2 != col % 2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int x = int(fragCoord.x);
    int y = int(fragCoord.y);
    int sqSide = 128;
    int row = y / sqSide;
    int col = x / sqSide;

    bool isBlack = isBlackSource(row, col);

    float pixelBoundaryPercentage = 10.0/100.0;
    float speed = 2.0;
    float sine = sin(iDate.w * speed);
    int pixelDelta = int(pixelBoundaryPercentage * float(sqSide) * sine);

    int xmod = x%sqSide;
    int ymod = y%sqSide;

    bool originalBlack = isBlack;

    vec3 color;

    if(pixelDelta < 0) {
        if(!isBlack) {
            if(xmod < -pixelDelta || xmod > sqSide + pixelDelta || ymod < -pixelDelta || ymod > sqSide + pixelDelta) {
                isBlack = true;
            }
        }
    } else {
        if(isBlack) {
            if(xmod < pixelDelta || xmod > sqSide - pixelDelta || ymod < pixelDelta || ymod > sqSide - pixelDelta) {
                isBlack = false;
            }
        }
    }



    if(isBlack) {
        color = vec3(0.0);
    } else {
        color = vec3(1.0);
    }

    // Output to screen
    fragColor = vec4(color,1.0);
}
