void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // slightly larger like 0.55 can look better at *lower* resolutions
    #define outline_weight 0.53

    vec2 uv = fragCoord / iResolution.xy;

    vec3 bkgr = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));
    fragColor = vec4(bkgr, 1.0);

    float symbol = texture(iChannel0, uv).x;
    float w = texture(iChannel0, uv).w;
    float border = w * step(w, outline_weight) * step(symbol, 0.5);

    fragColor += vec4(symbol) - vec4(border);
}
