void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Hint, it's a few lines of code, and makes use of a tan function :)
    vec2 warpedUv = uv;

    float y_distortion = sin(mod(iTime*0.5, 1.2)); //center
    float stripeWidth = 0.08;

    if (warpedUv.y > y_distortion-0.05 && warpedUv.y < y_distortion+0.05)
    {
        float offset = warpedUv.y;
        warpedUv.x += tan(offset*250.) * 0.1 + 0.1;
    }


    vec2 offset1 = vec2(0.);
    vec2 offset2 = vec2(.01);
    vec2 offset3 = vec2(-.01);

    float r = texture(iChannel0, warpedUv + offset1).r;
    float g = texture(iChannel0, warpedUv + offset2).g;
    float b = texture(iChannel0, warpedUv + offset3).b;

    vec3 col = vec3(r, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
