#define PI 3.1415926

const vec3 COLOR1 = vec3(1.0, 1.0, 1.0);


/**
*a:弧度值
*/
mat2 getRot(float _angle){
    vec2 value = sin(vec2(PI/2.0,0.0)+_angle);
    return mat2(value,-value.y,value.x);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv =  fragCoord/iResolution.xy;
    vec3 image = texture(iChannel0, uv).xyz;

    vec2 a = vec2(0.25, 0.2); // 渐变起始坐标.
    vec2 b = vec2(0.00, 0.2); // 渐变结束坐标.
    vec2 a1 = vec2(1.0-a.x, a.y); //渐变起始坐标.
    vec2 ba =  b-a;
    vec2 ba2 =getRot(PI)*ba;
    float t = dot(uv - a, ba) / dot(ba, ba);
    float t2 = dot(uv - a1, ba2) / dot(ba2, ba2);
    t = smoothstep(0.0, 1.0, clamp(t, 0.0, 1.0));
    t2 = smoothstep(0.0, 1.0, clamp(t2, 0.0, 1.0));
    t*=abs(sin(iTime*2.0)*1.0);
    if(uv.x < 0.5){
      vec3 finalColor = mix(image,COLOR1,t);
      fragColor = vec4(finalColor,1.0);
    }else{
      vec3 finalColor2 = mix(image,COLOR1,t2);
      fragColor = vec4(finalColor2,1.0);
    }

}
