// variant of https://shadertoy.com/view/ds23zW

#define cross(a,b)  ( (a).x*(b).y - (a).y*(b).x )
#define rot(a)      mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage( out vec4 O, vec2 u )
{
    float s = sqrt(3.)/2., i=0., a,b,
          T = mod(iTime*4.,20.); T = min(T,20.-T);
    vec2  R = iResolution.xy,
          U = 1.3*( u - .5*R ) / R.y + vec2(0,.45),
          S = vec2(.5/s,0), Y = vec2(0,1);

    for ( O=vec4(1); i < T; i++ ) {
        a = -cross( U-S, vec2(-.5,s) ),           // distance to right border
        b =  cross( U+S, vec2( .5,s) );           // distance to left border
        if ( U.y<0. || a < 0. || b < 0. ) return; // out of triangle
        U = U.y > .67 ? ( U-.67*Y ) *3.           // top triangle
          :  b  > .67 ? ( U-.67*S ) *3.           // right triangle
          :  a  > .67 ? ( U+.67*S ) *3.           // left triangle
          :    (U-.33*Y) *rot(-.524) / (.67*s) + .33*Y; // central triangle
        O *= vec4(.7,.8,.9,0);
    }
}
