// Another lyapunov kaleidoscope
// Based on https://www.shadertoy.com/view/WdscWH

#define PI	3.14159265359

#define cart_to_polar(cart) vec2(atan(cart.y, cart.x), length(cart))

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = 2.0 * (fragCoord / iResolution.xy) - 1.0;
	uv.x *= iResolution.x / iResolution.y;
	float wedge = PI / 9.0;
	float angle = abs(mod(atan(uv.x, uv.y), 2.0 * wedge) - wedge) - 0.785398;
	uv = length(uv) * vec2(cos(angle), sin(angle)) * 0.5;
	uv = cart_to_polar(uv);    
	vec3 col = texture(iChannel0, uv).xyz;
    fragColor = vec4(col, 1.0);
}

#define PI 3.14159265359

#define ANIMSPEED 0.1
#define ITERATIONS 20

// Modify sequence here, for example: AABAB => SEQLEN=5 - (0, 0, 1, 0, 1)
#define SEQLEN 9
int seq[] = int[SEQLEN](0, 0, 1, 0, 0, 0, 1, 1, 1);

vec3 calc(vec2 p)
{
    float t = iTime * ANIMSPEED;
    float w[] = float[ITERATIONS] (
		0.97 + 0.04 * sin(0.0 + 1.3 * t),
		0.97 + 0.04 * sin(1.0 + 1.7 * t),
		0.97 + 0.04 * sin(4.0 + 1.1 * t),
		0.97 + 0.04 * sin(2.0 + 1.5 * t),
		0.97 + 0.04 * sin(5.0 + 1.9 * t),
		0.97 + 0.04 * sin(6.0 + 1.8 * t),
		0.97 + 0.04 * sin(7.0 + 1.6 * t),
		0.97 + 0.04 * sin(8.0 + 1.3 * t),
		0.97 + 0.04 * sin(9.0 + 1.7 * t),
		0.97 + 0.04 * sin(10.0 + 1.1 * t),
		0.97 + 0.04 * sin(11.0 + 1.5 * t),
		0.97 + 0.04 * sin(12.0 + 1.9 * t),
		0.97 + 0.04 * sin(13.0 + 1.8 * t),
		0.97 + 0.04 * sin(14.0 + 1.6 * t),
		0.97 + 0.04 * sin(15.0 + 1.3 * t),
		0.97 + 0.04 * sin(16.0 + 1.7 * t),
		0.97 + 0.04 * sin(17.0 + 1.1 * t),
		0.97 + 0.04 * sin(18.0 + 1.5 * t),
		0.97 + 0.04 * sin(29.0 + 1.9 * t),
		0.97 + 0.04 * sin(20.0 + 1.8 * t)
	);

	float x = 0.5;
	float h = 0.0;
	for (int i = 0; i < ITERATIONS; i++)
	{
		for (int j = 0; j < SEQLEN; ++j)
		{
			x = w[j] * p[seq[j]] * x * (1.0 - x);
			h += log2(abs(w[j] * p[seq[j]] * (1.0 - 2.0 * x)));
		}
	}
	h /= float(ITERATIONS) * float(SEQLEN);

	vec3 col = vec3(0.0, 0.0, 0.0);

	if (h < 0.0)
	{
		h = abs(h);
		vec3 shift = vec3(0.8, 0.55, 0.0033) + t;
		vec3 col1 = cos((shift + 2.5 * h) * PI) * 0.5 + 0.5;
		vec3 col2 = vec3(0.5, 0.5, 0.5) + 0.5 * sin(vec3(0.3, 0.33, 0.3) + 2.5 * h + t * 0.5);
		col = mix(col1, col2, pow(h, 0.625));
	}

	return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 col = calc(3.0 + (fragCoord / iResolution.xy));
    fragColor = vec4(col, 1.0);
}
