#define R iResolution
#define PI 3.14159265359
void mainImage( out vec4 o, in vec2 p )
{
    vec2 u = (p * 2.0 - R.xy) / R.y;
    float t = atan(u.x,u.y)/PI;
    float c = fract(t*20.0+sqrt(length(u))*10.0-iTime*3.0);
    c = c<0.5?c*2.0:(1.0-c)*2.0;
    c = 0.2+pow(c,3.0)*0.6+0.2*sin(u.x+u.y+iTime);
    o = vec4(c,c,c,1.0);
}
