void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0,fragCoord/iResolution.xy);
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    float o = +A(u+vec2(-2,-1)).x
              +A(u+vec2(-1,-1)).x
              +A(u+vec2( 0,-1)).x
              +A(u+vec2( 1,-1)).x
              +A(u+vec2( 2,-1)).x
              +A(u+vec2( 0,-2)).x;

    float s = floor(u.x/iResolution.x*4.);
         uint id = 0U;         //world ID
    if(s==0.){id = 16U*2U+4U;}
    if(s==1.){id = 16U*3U+2U;}
    if(s==2.){id = 16U*3U+3U;}
    if(s==3.){id = 16U*3U+4U;}

    o = float((id>>int(o+.1))&1U);

    if(iFrame==0||iMouse.z>.5)
    {
        o = floor(fract(cos(dot(u,vec2(234.76543,iTime+11.23513)))*3467.5678)+.5);
    }
    if(iFrame!=0&&int(u.y)!=(iFrame%int(iResolution.y))){o=A(u).x;}
    fragColor = vec4(o);
}
