//Floating Lands by kastorp
//------------------------------


//  Shane - https://www.shadertoy.com/view/lstGRB
float n31(vec3 p) {
	const vec3 s = vec3(7, 157, 113);
	vec3 ip = floor(p);
	p = fract(p);
	p = p * p * (3. - 2. * p);
	vec4 h = vec4(0, s.yz, (s.y+s.z)) + dot(ip, s);
	h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

float map2d(int i,float ns,float ds){         
    return(ns-.5 -.6+float(i)/12.)*ds;
}

float map (vec3 ps){
    float bottom=0., sz=.3;
     float  d=1e5, sg=1., ds=.5;
    int N =6;

    ps.y-= n31(vec3(ps.xz*01./ds,3. 
         +(ps.y<0.?sin(iTime/20.)*20.:0.) //waves
        ))*.3;
    vec2 p=vec2(ps.y,0);

    float   ns= n31(vec3(ps.xz*1./ds,1.));

    float  yn=map2d(N,ns,ds);
    for(int i=0;i<N;i++){   
        float y0=map2d(i,ns,ds),
              y1=map2d(i+1,ns,ds);
        float  a= 0.,
               c=y0,
               b=(y1-y0)/sz,
               shift=float(i)*sz;
        //K<0 inflate, k>0 deflate
        float k=.06 ; a+= k/sz/sz; b-= k/sz ;


        vec2 pp=p-vec2(shift+bottom,0);
        if(pp.x>0. && pp.x<sz && pp.y<c+pp.x*b+pp.x*pp.x*a) sg=-1.; //sign

        float xx=clamp(p.x-shift-bottom,0.,sz),
              yy=c+xx*b +xx*xx*a;            
        d = min(d, length(p-vec2(xx+shift+bottom,yy)));       
    }
     float top=bottom+float(N)*sz;
    d=min(d, length(p- vec2(top,yn) +vec2(0,1)*  clamp(vec2(top,yn)-p.y  , 0., 1e6)));

    d*=sg;
    d=min(d,ps.y+1.);
    return d;
}



//-------------------------

//Iq
vec3 calcN(vec3 p, float t) {
	float h = .001 * t;
	vec3 n = vec3(0);
	for (int i = min(iFrame, 0); i < 4; i++) {
		vec3 e = .5773 * (2. * vec3((((i + 3) >> 1) & 1), (i >> 1) & 1, i & 1) - 1.);
		n += e * map(p + e * h);
	}
	return normalize(n);
}


//Iq
vec3 vignette(vec3 c, vec2 fc) {
	vec2 q = fc.xy / iResolution.xy;
	c *=  .5 + .5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), .4);
	return c;
}

vec3 getRayDir(vec3 ro, vec3 lookAt, vec2 uv) {
	vec3 f = normalize(lookAt - ro),
		 r = normalize(cross(vec3(0,1., 0), f));
	return normalize(f + r * uv.x + cross(f, r) * uv.y);
}


vec3 render(vec3 ro, vec3 rd) {
	vec3 p;
	float d = .01,ds=.001,h;
	for (float i = 0.; i < 100.; i++) {
		p = ro + rd * d;
		h = map(p)*.9;
		if (abs(h) < .001  )break;
        if(d >25.) return vec3(0.400,0.576,0.576);
		d += h;
	} 

    vec3 col= p.y>1.8?vec3(0.020,0.071,0.008):p.y<.01?vec3(0.004,0.012,0.035):vec3(0.020,0.020,0.020)+.02* cos(vec3(0,2,4)-fract(p.y/.3));
    vec3 ld = normalize( vec3(6.,19.,18.) ),

        n = calcN(p, d) ;
	float ao = 1.,
	l1 = .5 + .5 * dot(ld, n),
	spe = max(0., dot(rd, reflect(ld, n))) * .2,
	fre = smoothstep(.7, 1., 1. + dot(rd, n));

    vec3 po= p+.003*n;
	for (float i = 0.; i < 40.; i++) {
		p = po + ld * ds;
		h = map(p);
		if (h < .001  )break;
        if(ds >10.) break;
		ds += h;
	}     

	l1 *= .2+ .8 *step(.01,h);
	vec3 lig = clamp(l1 * ao + spe,0.,5.) * vec3(4.) ;
	lig=  mix(.3, .4, fre) * lig;
    return mix(vec3(0.078,0.463,0.463), col*lig,exp(-d*d * .004));
}



void mainImage(out vec4 fragColor, vec2 fc)
{	
    float t=-iTime*1.5;
    vec2 m= iMouse.x>.0? -1.+2.*iMouse.xy/iResolution.xy:vec2(0);
	vec3 ro = vec3(t,2.5 +2.*m.y, 4.*m.x);	
	vec2 uv = (fc - .5 * iResolution.xy) / iResolution.y;
	fragColor = vec4(vignette(pow(render(ro, getRayDir(ro, vec3(t-3.,1.,0), uv)), vec3(.4)), fc), 0);
}
