float z = 20., amp = 2.2;
float fun(float x){
    return amp*(sin(x+iTime)+sin(x/2.-2.-iTime)+sin(x/4.-4.+iTime*0.5));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec3 col = vec3(0);
    vec2 uv = (fragCoord.xy / iResolution.y - 0.5)*z;
    vec2 muv = (iMouse.xy / iResolution.y - 0.5)*z;
    float f = fun(uv.x);
    col += vec3(0,1,0) * .1/abs(uv.y-f);
    float k = amp*(cos(muv.x+iTime)+cos(muv.x/2.-2.-iTime)*0.5+cos(muv.x/4.-4.+iTime*0.5)*0.25);
    col += vec3(0,0,1) * .1/abs(uv.y-(k*(uv.x-muv.x)+fun(muv.x)));
    col += vec3(1,0,0) * .05/(pow(muv.x-uv.x, 2.)+pow(fun(muv.x)-uv.y, 2.));
    fragColor = vec4(col, 1.0);
}
