// twinkle speed
#define ts 2.22e-4

// star count (higher is less)
#define sc 42.

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv + vec2( 0.5 );
    float t = iTime * ts;
    p *= 10./7. + uv * sin(t) * cos(t);
    float a = length( texture(iChannel0, p, -200.).xy);

    a = clamp(pow(a, sc),0.,0.9);

    float w = texture(iChannel0, uv, -100.).x - 0.5;
    float r = w > 0. ? a + 0.1 * w : a;
    float b = w < 0. ? a - 0.1 * w : a;

    // Time varying pixel color
    vec3 col = vec3(r, a, b);

    // Output to screen
    fragColor = vec4(col,a);
}
