#define PI 3.14159265359

// Author: Elio Ramos (@garabatospr instagram, twitter) 


// color palette

const vec3 COLORS[14]=vec3[14](
	vec3(0.949,0.922,0.541),
    vec3(0.996,0.816,0.),
    vec3(0.988,0.518,0.0),
    vec3(0.929,0.212,0.102),
    vec3(0.886,0.941,0.953),
    vec3(0.702,0.863,0.878),
    vec3(0.267,0.392,0.631),
    vec3(0.125,0.188,0.318),
    vec3(1.,0.773,0.78),
    vec3(0.953,0.596,0.765),
    vec3(0.812,0.22,0.584),
    vec3(0.427,0.208,0.541),
    vec3(0.024,0.706,0.69),
    vec3(0.294,0.541,0.373)  
);


float Hash21(vec2 p) {
    p = fract(p*vec2(234.234, 435.145));
    p += dot(p, p+34.236767);
    return fract(p.x*p.y);
}


mat2 rotate2d(in float radians){
  float c = cos(radians);
  float s = sin(radians);
  return mat2(c, -s, s, c);
}

// get "random" color based on location 

vec3 getColor(vec2 id,float seed)
{
  return COLORS[int(14.*Hash21(id*seed))];
}


vec3 drawConcentric(vec2 st,vec3 col)
{

    //st.x *= 5./1.8;
    //st.y *= 5.0;

    st -= 0.5;

    st *= 3.;

    vec2 id  = floor(st)+0.5;

    vec2 fst = fract(st)-0.5;


    fst *= rotate2d(PI/2.0*sign(Hash21(id)-0.5)*iTime);


    float r = length(fst);

    float blur = 0.2;
    float rad = 0.3;

    vec3 col1 = getColor(id,124.);
    vec3 col2 = getColor(id,566.);
    vec3 col3 = getColor(id,667.);

    vec3 col4 = getColor(id,158.);
    vec3 col5 = getColor(id,879.);
    vec3 col6 = getColor(id,910.);

    // half concentric blur

    col = mix(col1,col,smoothstep(rad-blur,rad,r));
    col = mix(col2,col,smoothstep(rad/1.5-blur,rad/1.5,r));
    col = mix(col3,col,smoothstep(rad/3.-blur,rad/3.,r));

    // half concentric sharp 

    col = mix(col4,col,smoothstep(rad - 0.01,rad,r)+step(0.0,fst.y));
    col = mix(col5,col,smoothstep(rad/1.5 - 0.01,rad/1.5,r)+step(0.0,fst.y));
    col = mix(col6,col,smoothstep(rad/3.0 - 0.01,rad/3.0,r)+step(0.0,fst.y));

    return col;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.0);

    col = drawConcentric(st,col);

    fragColor = vec4(col,1.0);
}
