void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//get the current time in seconds
#define iGlobalTime (float(iFrame)/60.0)
float time = iGlobalTime;

//normalize the fragCoord to be between 0 and 1
vec2 uv = fragCoord/iResolution.xy;

//create the city skyline by using a smoothstep function
float skyline = smoothstep(0.3, 0.7, uv.y);

//add multicolored lights to the skyline using the sin and cos functions
vec3 lights = vec3(sin(time + uv.x), sin(time + uv.y), cos(time + uv.x));

//add a sunset gradient in the background using a mix function
vec3 sunset = mix(vec3(0.9, 0.6, 0.2), vec3(0.1, 0.1, 0.1), uv.y);

fragColor = vec4(lights * skyline + sunset, 1.0);
}
