void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float strobeFrequency = 10.0;
  float colorSpeed = 2.0;

  // Compute the strobe value
  float strobeValue = sin(iTime * strobeFrequency);

  // Use the strobe value to create a colorful gradient
  fragColor = vec4(abs(sin(strobeValue * colorSpeed)), abs(cos(strobeValue * colorSpeed)), abs(sin(strobeValue * colorSpeed + 1.0)), 1.0);
}
