float sdHexagon( vec2 p, float s, float r ) 
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*s, k.z*s), s);
    return length(p)*sign(p.y) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy *2. - 1.;
    uv.x *= iResolution.x / iResolution.y;
    vec3 fColor = vec3(1.0);
    fColor = vec3(sdHexagon(uv, 0.4+.2, -0.4+.2))* sin(iTime);
    fColor = smoothstep(.0,.4,fColor);
    fColor = 0.1 / fColor;

    if(fColor[0] > .9) {
        fColor = .1 / vec3(sdHexagon(uv, 0.4, -0.4))* sin(iTime);
    }

    fragColor = vec4(fColor*vec3(.0,1.,0.), 1.0);
}
