void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 screenSize = iResolution.xy;
    // Time varying pixel color
    vec3 col = vec3(0, 0, 0);
    for(float i = 0.0; i < 3000.0; i++)
    {
        float theta = i * (137.0 + (iTime * 0.005));
        float r = (10.0) * sqrt(i);
        vec2 circlePos = vec2(cos(theta*3.14159/180.0) * r, sin(theta*3.14159/180.0) * r) + vec2(screenSize/vec2(2,2)) ;
        if(length(fragCoord-circlePos) < 5.0)
            col =  0.5 + 0.5*cos((iTime*0.75)+uv.xyx+vec3(0,2,4));


    }
    // Output to screen

    fragColor = vec4(col,1.0);
}



