void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Bildschirmauflösung
    vec2 resolution = iResolution.xy;

    // Kugelposition
    vec2 position = fragCoord.xy / resolution.xy;
    position = position * 2.0 - 1.0;

    // Farbverlauf
    vec3 color = vec3(1.0);
    color *= 0.5 + 0.5 * cos(iTime);

    // Sound-Input
    float sound = texture(iChannel0, vec2(0.5,0.5)).r;
    float soundLevel = pow(sound, 3.0) * 2.0;

    // Kugelradius
    float radius = 0.3 + soundLevel * 0.3;

    // Distanz von der Kugelmitte
    float distance = length(position);

    // Kugel zeichnen
    if (distance < radius) {
        color *= pow(1.0 - distance/radius, 2.0);
        fragColor = vec4(color, 1.0);
    } else {
        discard;
    }
}
