void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 6.+cos(iTime)/3.; // zoom

    // calculate 3 seperate colour channel patterns
    float r=1.6*tan(cos(uv.x)+cos(uv.y));
    float g=0.8*tan(cos(3.*uv.x)+cos(4.*uv.y));
    float b=r/.1;

    if (r>1.0 && r<32.) r=floor(r-(1.*floor(r/1.)*3.))/2.;
    //if (g>1.0 && g<32.) g=floor(g-(1.*floor(g/1.)*3.))/2.;
    if (b>1.0 && b<32.) b=floor(b-(1.*floor(b/1.)*3.))/2.;
    //if (r>1.0 && r<32.) r=r-(1.*floor(r/1.));
    if (g>1.0 && g<32.) g=g-(1.*floor(g/1.));
    //if (b>1.0 && b<32.) b=b-(1.*floor(b/1.));

    r=r*.4; g=g*.8; b=b*.8;

    vec3 col = cos(iTime/.7+(1.-length(uv))*6.+vec3(r,g,b)); // Time varying pixel color

    fragColor = vec4(col,1.);
}
