void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Half width for rectangle to illuminate.
    float aperture = 0.05;
    // All measured in frames:
    int on_time = 30;
    int off_time = 30;
    int period = on_time + off_time;

    // Start with background color.
    vec3 col= vec3(0.0, 0.0, 0.0);
    // Then choose on/off colors for rectangle.
    vec3 on_color = vec3(0.0, 0.9, 0.0);
    vec3 off_color = vec3(0.0, 0.1, 0.0);

    // Normalized pixel coordinates (from 0 to 1).
    vec2 uv = fragCoord/iResolution.xy;
    if (uv.x > 0.5 - aperture &&
        uv.x < 0.5 + aperture &&
        uv.y > 0.5 - aperture &&
        uv.y < 0.5 + aperture) {
        if (iFrame % period < on_time) {
            col = on_color;
        } else {
            col = off_color;
        }
    }
    // Output to screen.
    fragColor = vec4(col,1.0);
}
