void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    float angle = atan(p.y, p.x);
    float radius = length(p);

    // Параметры donut
    float donutRadius = 15.23;
    float tubeRadius = 16.11;

    // Управляем толщиной donut
    float thickness = 2.25;  // Увеличиваем толщину в 3 раза

    // Вращение вокруг оси
    float rotation = iTime * 2.5;

    // Положение вращающегося donut
    float donutX = cos(rotation) * (radius - donutRadius);
    float donutY = sin(rotation) * (radius - donutRadius);

    // Расстояние от текущей точки до donut
    float distanceToDonut = length(vec2(donutX, donutY) - vec2(radius, angle));

    // Рассчитываем alpha для двухцветности
    float alpha = smoothstep(donutRadius - thickness, donutRadius, distanceToDonut) -
                  smoothstep(donutRadius, donutRadius + thickness, distanceToDonut);

    // Устанавливаем цвет
    vec3 color = vec3(1.0, 0.0, 0.0);  // Красный
    color = mix(color, vec3(0.0, 1.0, 0.0), alpha);  // Смешиваем с зеленым

    // Отображаем donut
    fragColor = vec4(color, 1.0);
}
