float magnitude(vec2 imNumber) 
{
    return sqrt(imNumber.x * imNumber.x + imNumber.y * imNumber.y);
}

float mandelbrot(vec2 c, int N)
{
    vec2 z = vec2(0);
    int n = 0;

    for (int i = 0; i < N; i++)
    {
        if (magnitude(z) > 2.)
        {
            break;
        }

        float z0 = z.x * z.x - z.y * z.y;
        float z1 = 2. * z.x * z.y;
        z = vec2(z0 + c.x, z1 + c.y);

        n++;
    }

    return float(n) / float(N);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv.x = 3. * uv.x - 2.;
    uv.y = 2. * uv.y - 1.;

    float m_val = mandelbrot(uv.xy, int(4. * iTime));
    vec3 col = vec3(.8 * m_val, .8 * acos(1. - m_val), exp(-m_val));

    fragColor = vec4(col, 1.0);
}
