#define sat(a) clamp(a, 0., 1.)

// Thanks IQ :)
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}
// Thanks IQ :)
float sdBoxFrame( vec3 p, vec3 b, float e )
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}
mat2 r2d(float a) { float c = cos(a), s = sin(a); return mat2(c, -s, s,c);}
float map(vec3 p)
{
    vec3 p2 = p;
    p2.yz *= r2d(p.x+iTime);
    float bend = sdBox(p2.yz, vec2(.5));
    p.xy *= r2d(iTime);
    p.xz *= r2d(iTime);
    float wire = sdBoxFrame(p, vec3(1.), .05);
    return min(wire, bend);
}
float rdr(vec2 uv)
{
    vec3 ro = vec3(0.,0.,-5.);
    vec3 rd = normalize(vec3(uv, 1.));

    float col = 0.;
    vec3 p = ro;
    for (float i = 0.; i < 128.; ++i)
    {
        float d = map(p);
        if (d < 0.01)
        {
            col = pow(1.-sat(distance(p, ro)/15.),.8);
            break;
        }
        p+=rd*d;
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;

    vec3 col = vec3(1.);
    vec2 rep = vec2(.015);
    vec2 id = floor((uv+rep*.5)/rep);
    uv = mod(uv+rep*.5,rep)-rep*.5;
    vec3[5] cols = vec3[5](
        vec3(0.93f, 0.93f, 0.93f),
        vec3(0.84f, 0.9f, 0.46f),
        vec3(0.55f, 0.78f, 0.33f),
        vec3(0.27f, 0.64f, 0.16f),
        vec3(0.13f, 0.41f, 0.07f)
    );
    vec2 sz = rep*.35;
    float shape = sdBox(uv, sz);
    float val = rdr(id*rep);
    col = mix(col, cols[int(val*4.)], 1.-sat(shape*iResolution.x));
    fragColor = vec4(col,1.0);
}
