void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 1.0;

    vec3 pos = vec3((fragCoord.xy / iResolution.xy) - 0.5, 0.0) * 1.0;

    float wave =tan(pos.x * 5.0 + time *0.0) + tan(pos.y * 5.0 + time * 0.0) + cos(pos.z * 5.0 + time * 50.0);
    pos.z = wave * 1.0;

    vec3 color = vec3(0.0);
    color.r = abs(pos.x) + 100.0*wave;
    color.g = abs(pos.y) + wave;
    color.b = abs(pos.x) + wave;

    vec3 light = vec3(1.0, 1.0, 1.0);
    float intensity = dot(normalize(pos), normalize(light));
    color += pow(intensity, 4.0);

    fragColor = vec4(color, 1.0);
}
