float rand(vec2 n) {
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 x) {
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f * f * (3.0 - 2.0 * f);
    return mix(mix(rand(p), rand(p + vec2(1.0, 0.0)), f.x),
               mix(rand(p + vec2(0.0, 1.0)), rand(p + vec2(1.0, 1.0)), f.x), f.y);
}

float fbm(vec2 x, float time) {
    float value = 0.0;
    float amplitude = 0.4;

    for (int i = 0; i < 6; i++) {
        value += amplitude * noise(x + time * (0.5 + float(i) * 0.1));
        x *= 2.0;
        amplitude *= 0.5;
    }

    return value;
}

vec3 cloudGradient(float t) {
    vec3 startColor = vec3(0.8, 0.9, 1.0);
    vec3 midColor = vec3(0.6, 0.75, 0.9);
    vec3 endColor = vec3(0.2, 0.3, 0.5);
    return mix(mix(startColor, midColor, t), endColor, t * t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    float time = iTime;

    float n = fbm(uv * 4.0, time * 0.3);
    float m = fbm(uv * 24.0, time * 0.45);

    float cloudDensity = smoothstep(0.4, 0.5, n + m * 0.5);

    vec3 color = cloudGradient(cloudDensity);

    fragColor = vec4(color, 1.0);
}
