  } else if (idObj == idRoad) {
    g = abs (fract (qHit.xz) - 0.5);
    if (g.x < g.y) g = g.yx;
    col4.rgb = mix (vec3 (0.1, 0.1, 0.12), vec3 (0.1), step (g.x, 0.355));
    f = ((step (abs (g.x - 0.495), 0.002) + step (abs (g.x - 0.365), 0.002)) +
       step (abs (g.x - 0.44), 0.0015) * step (fract (g.y * 18. + 0.25), 0.7)) *
       step (g.y, 0.29);
    col4.rgb = mix (col4.rgb, vec3 (0.6, 0.5, 0.1), f);
    f = step (0.6, fract (g.x * 30. + 0.25)) * step (0.36, g.x) * step (abs (g.y - 0.32), 0.02);
    col4 = mix (col4, vec4 (0.7, 0.7, 0.7, 0.2), f);
    g = abs (g) - 0.493;
    f = step (0., Minv2 (g));
    col4 = mix (col4, vec4 (0.7, 0.7, 0.7, 0.2), f);
    if (fDayLt > 0.5) {
      b = CarPos (ro).xz;
      g = abs (b + vec2 (0., -0.1)) - vec2 (0.015, 0.2);
