      if (g.x < g.y) g = g.yx;
      f += step (g.y, 0.35) * (1. - smoothstep (0.02, 0.05,
         length ((vec2 (0.8 * abs (g.x - 0.437) - 0.015, mod (g.y + 0.05, 0.1) - 0.05)))));
      col4.rgb = mix (col4.rgb, vec3 (1., 1., 0.7), 0.2 * f);
    }
  } else if (idObj == idSWalk) {
    g = step (0.05, fract (qHit.xz * 35.));
    col4.rgb = vec3 (0.2) * mix (0.7, 1., g.x * g.y);
    if (bHt > 0.) {
      f = ((bShape > 0.25) ? max (abs (qHit.x), abs (qHit.z)) : length (qHit.xz)) - bWid;
      if (f < 0.02) col4.rgb *= 2.;
      else if (f < 0.025) col4 = vec4 (vec3 (1., 1., 0.7) * max (0.5 - 0.5 * rd.y, 0.), -1.);
    } else {
      if (length (abs (qHit.xz) - 0.21) < 0.01) col4.rgb *= 0.7;
    }
  } else if (idObj == idTrLight) {
    f = 2. * (atan (qHit.z, qHit.x) / pi + 1.) + 0.5;
    ff = floor (f);
    y = qHit.y - 0.014;
    if (abs (y) < 0.004 && abs (f - ff) > 0.3) {
      col4 = vec4 (mix (vec3 (0., 1., 0.), vec3 (1., 0., 0.), step (0., y)), -1.);
      col4.rgb *= 1. - 0.8 * step (0., y * sign (cDir - 0.5) * sign (mod (ff, 2.) - 0.5));
