    } else {
      col4 = vec4 (vec3 (0.5, 0.5, 0.55) * (1. + 0.3 * sin (128. * 2. * pi * qHit.y)), 0.5);
    }
  } else if (idObj == idPool) {
    col4 = (length (qHit.xz) < 0.18) ? vec4 (0.4, 0.4, 0.5, -2.) :
       vec4 (0.2, 0.3, 0.5, 0.1) * (0.6 + 0.4 * fDayLt);
  } else if (idObj == idTrnk) {
    col4 = vec4 (0.4, 0.2, 0., 0.1);
  } else if (idObj == idTree) {
    col4 = vec4 (0., 0.5, 0.1, 0.1);
  } else if (idObj == idCarBdy || idObj == idCarWhl) {
    col4 = CarCol ();
    if (idObj == idCarBdy && fDayLt < 0.5 && col4.a > 0.) {
      g = abs (fract (ro.xz) - 0.5);
      f = 1.3 * (1. - smoothstep (0.05, 0.2, length (g - 0.5)));
      if (g.x < g.y) g = g.yx;
      f += step (g.y, 0.35) * (1. - smoothstep (0.02, 0.05, length ((vec2 (0.8 *
         abs (g.x - 0.437) - 0.015, mod (g.y + 0.05, 0.1) - 0.05)))));
      col4.rgb = mix (col4.rgb, vec3 (0.7, 0.7, 0.5), 0.4 * f);
    }
  }
  return col4;
}

