const vec3 COLOR15 = vec3(62,119,254) / 255.; // 7.5
const vec3 COLOR45 = vec3(102,132,213) / 255.;  // 22.5
const vec3 COLOR110 = vec3(68,67,102) / 255.;// 55

const float PI = 3.1415926;
const float trans = PI / 180.;

float remap(float value,float start1,float stop1,float start2,float stop2){
    return start2 + (value - start1) * (stop2 - start2) / (stop1 - start1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // u 轴表示 中间矢量H 与 表面法线N 之间的角度
    float u = uv.x;
    vec3 colorU = vec3(0);
    if (u < (15. / 2. / 90.)) {
        colorU = COLOR15;
    } else if (u < (45. / 2. / 90.)) {
        colorU = mix(COLOR15, COLOR45, remap(u, (15. / 2. / 90.),(45. / 2. / 90.), 0., 1.));
    } else if (u < (110. / 2. / 90.)) {
        colorU = mix(COLOR45, COLOR110, remap(u, (45. / 2. / 90.),(110. / 2. / 90.), 0., 1.));
    } else {
        colorU = COLOR110;
    }

    // Output to screen
    fragColor = vec4(colorU,1.0);
}
