float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}
vec3 palette(float t){
    vec3 a = vec3(-0.280, 0.500, 0.500);
    vec3 b = vec3(0.970, 0.500, 0.500);
    vec3 c = vec3(0.968, 1.038, 1.000);
    vec3 d = vec3(1.800, 0.333, 0.667);
    return a + b*cos( 6.28318*(c*t*d));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
 vec2 uv0 = uv;
 uv = fract(uv*2.0)-0.5;
 float d = sdCircle(uv,0.5);
 vec3 scol = palette(iTime/2.0);

 vec3 col = (d<0.0) ? scol : vec3(1.0,1.0,1.0);
 fragColor = vec4(col,1.0);

}
