#define max_radius 13.0
#define T 10.0
#define dt 0.1
#define k_radii vec2(3./4., 1.)
#define k_amount 2.0
#define mu 0.1
#define sigma 0.005
#define rho 0.5 
#define omega 0.15
#define noise_base 0.15

float growth(float sum) {
    return gaussian_bell(sum, mu, sigma) * 2.0 - 1.0;
}

float target(float sum) {
    return gaussian_bell(sum, mu, sigma);
}

float getWeight(float x, float y) {
    float r = sqrt(x * x + y*y) / max_radius; 
    r *= k_amount; 
    float radius = k_radii[int(r)]; 
    if ((r / k_amount) <= 1.) return radius * gaussian_bell(mod(r, 1.), rho, omega);
    return 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord.xy / iResolution.xy;
    float sum = 0.0;
    float total = 0.0;
    for(float x = -max_radius; x <= max_radius; x++) {
        for( float y = -max_radius; y <= max_radius; y++) {
            if (x == 0.0 && y == 0.0) {
                continue;
            }
        	vec2 offset = vec2(x, y) / iResolution.xy;		
        	vec4 lookup = texture(iChannel0, mod(uv + offset, 1.0));
            float weight = getWeight(x, y);
        	sum += lookup.x * weight;
            total += weight;
        }
    }
    sum /= total;

    float cell = texture(iChannel0, uv).x;
    float color = clamp(cell + dt * (target(sum) - cell), 0.0, 1.0);

    if (iMouse.z > 0.) {
        float d = length((fragCoord.xy - iMouse.xy) / iResolution.xx);
        if (d <= 6./iResolution.x)
        	color = 0.3 + noise(fragCoord/max_radius + mod(iDate.w,1.)*100.);
    }
    fragColor = vec4(color, color, color, 1.0);
}

