// referer: https://madethisthing.com/iq/Demo-Framework-4k/i1k_OGLShader

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 v=gl_FragCoord.xy/iResolution.xy*2.-1.;
    // tunnel
    float r=pow(pow(abs(v.x),18.)+pow(abs(v.y),10.),1.8/28.);
    vec2 t=vec2(iTime*0.5+1./r,atan(v.x,v.y));
    // texture
    t=fract(2.*t)-.5;
    vec4 col=(1.-pow(dot(t.xy,t.xy),.3))*vec4(2,1.8,1.6,0.)+vec4(.3,.2,.1,0.)+.12*vec4(v,0,0.);
    // final color
    fragColor=col/(2.*r);
}
