void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 a_n_pixel_coords_norm = fragCoord.xy / iResolution.xy; 

    float n_radius = 1.0;

    float n_tau = 6.2831;
    float n_amp = 0.01;
    vec2 a_n_translation = vec2(0.0,0.0);

    vec2 a_n_circle = vec2(
        cos(n_tau*a_n_pixel_coords_norm.x)*n_amp * n_radius + 0.5, 
        sin(n_tau*a_n_pixel_coords_norm.x)*n_amp * n_radius + 0.5
    ); 

    float n_distance = length((0.5,0.5) - a_n_pixel_coords_norm);
    n_distance -= mod(iTime*0.1, 1.0);
    float n_mod_max = 0.2; 
    float n_dist_mod = mod(n_distance, n_mod_max);
    float n_dist_mod_norm = n_dist_mod / n_mod_max;

    float n_dist_mod_norm_sin = sin(n_dist_mod_norm*n_tau)*0.5+0.5;

    fragColor = vec4(1.0-n_dist_mod_norm_sin);



}
