void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;    

    vec3 color = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));    

    float tickerx = iTime;
    float tickery = iTime/2.0;

    float multy=18.0+16.0*(sin(tickerx));
    float multx=64.0*(cos(tickery));

    vec2 offset = vec2(-0.5,-0.5);
    vec2 mult = vec2(multx,multy);

    uv =(uv+offset)*mult;


    float x = uv.x;
    float y = uv.y;


    vec4 yellow = vec4(vec3(0.0,87.0,255.0)/255.0,1.0);
    vec4 blue   = vec4(vec3(255.0,215.0,0.0)/255.0,1.0);

    float t = 0.5+0.5*cos(iTime);
    t=1.0-t;

    vec4 color1 = vec4(color,1.0) *t + (1.0-t)*yellow;
    vec4 color2 = vec4(1.0-color,1.0)*t + (1.0-t)* blue;  


     fragColor =  sin(x)/x > y ? color1 : color2;


    // Output to screen

}
