void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

     float n_y_scale_resolution_min = min(iResolution.x, iResolution.y);
     float n_y_scale_resolution_max = max(iResolution.x, iResolution.y);
     float n_scale_res_xy_delta = n_y_scale_resolution_max - n_y_scale_resolution_min;
     vec2 a_n_pixel_coords_norm_square_aspect_ratio = 
        (fragCoord.xy - vec2(n_scale_res_xy_delta/2.0,0)) 
        / 
        vec2(n_y_scale_resolution_min, n_y_scale_resolution_min);

     vec2 a_n_mouse_coords_norm_square_aspect_ratio = 
        (iMouse.xy - vec2(n_scale_res_xy_delta/2.0,0)) 
        / 
        vec2(n_y_scale_resolution_min, n_y_scale_resolution_min);

     if(iMouse.xy == vec2(0.0)){

         a_n_mouse_coords_norm_square_aspect_ratio = 
         vec2(
             sin(iTime*0.2)*0.1+0.5,
             0.5
         );
     }



    vec2 delta = vec2(
        a_n_pixel_coords_norm_square_aspect_ratio - vec2(0.5,0.5)
    ); 
    float n_circles = 60.0;
    float n_circle_width = 0.9*a_n_mouse_coords_norm_square_aspect_ratio.y;
    float dist = length(delta);
    float distmod = mod(dist, 1.0/n_circles)/(1.0/n_circles);
    float distmodstep = step(n_circle_width,distmod);
    fragColor = vec4(distmodstep);

    float n_circles2 = n_circles - 5.0;
    vec2 delta2 = vec2(
        a_n_pixel_coords_norm_square_aspect_ratio - vec2(a_n_mouse_coords_norm_square_aspect_ratio.x,0.5)
    ); 
    float dist2 = length(delta2);
    float distmod2 = mod(dist2, 1.0/n_circles2)/(1.0/n_circles2);
    float distmodstep2 = step(n_circle_width,distmod2);
    fragColor = vec4(mix(fragColor.r, distmodstep2, 0.5));

}
