#define GLOW_SAMPLES 80
#define GLOW_DISTANCE 0.2
#define GLOW_POW 1.1
#define GLOW_OPACITY 15.

#define sat(a) clamp(a, 0., 1.)
#define PI 3.14159265
#define TAU (PI*2.0)

mat2 r2d(float a) { float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }
float hash11(float seed)
{
    return fract(sin(seed*123.456789)*123.456);
}

vec2 _min(vec2 a, vec2 b)
{
    if (a.x < b.x)
        return a;
    return b;
}

float _cube(vec3 p, vec3 s)
{
    vec3 l = abs(p)-s;
    return max(l.x, max(l.y, l.z));
}

// This work is licensed under the Creative Commons Attribution-NonCommercial-ShareAlike 3.0
// Unported License. To view a copy of this license, visit http://creativecommons.org/licenses/by-nc-sa/3.0/ 
// or send a letter to Creative Commons, PO Box 1866, Mountain View, CA 94042, USA.
// =========================================================================================================
#define FFT(a) (texture(iChannel3, vec2(a,0.)).x*.75)
float _seed;
float rand()
{
    _seed++;
    return hash11(_seed);
}
vec3 getCam(vec3 rd, vec2 uv)
{
    float fov = 4.;
    if (iTime > 104. && iTime < 174.)
        fov = 2.+sin(iTime*.3);
    vec3 r = normalize(cross(rd, vec3(0.,1.,0.)));
    vec3 u = normalize(cross(rd, r));
    return normalize(rd+fov*(r*uv.x+u*uv.y));
}
vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.,-1.);

    acc = _min(acc, vec2(length(p+vec3(2.,-3.8,-4.))-2.5, 0.));

    float cube = -_cube(p, vec3(6.));
    acc = _min(acc, vec2(cube, 1.));

    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.001, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps && distance(p, ro) < 20.; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.001)
            return vec3(res.x, distance(p, ro), res.y);
        p+=rd*res.x;
    }
    return vec3(-1.);
}

vec3 getMat(vec3 p, vec3 n, vec3 rd, vec3 res)
{
    vec3 col = n*.5+.5;
    vec3 emit = vec3(0.);
    if (res.z == 1.)
    {
        col = vec3(0.);
        if (sin(iTime+FFT(.1)*13.) > 0.1)
        {
        if (p.y < -5.)
        {
            emit = vec3(1.)*sat(sin(p.z*5.+iTime));
            float beat = 1./8.;

            emit *= mix(1.,.2,(mod(iTime, beat)/beat)*sat(10.*sin(iTime)));
        }
        }
        else
        {
            if (p.y > 5.5)
            {
                vec2 luv = p.xz;
                float pix = 1.5;
                luv = floor(luv/pix)*pix;
                vec3 rgb = mix(vec3(0.1,.1,1.), vec3(1.000,0.506,0.102)*2., sat(-.5+sin(length(luv)*5.-5.*iTime)));
                emit = rgb*sat(sin(p.z*3.)*sin(p.x*3.)*10.);
            }
        }
    }
    if (res.z == 0.)
    {
        col = vec3(0.);
        float freq = 10.;
        float stpt = 2.;
        float t = floor(5.*iTime/stpt)*stpt;
        vec3 rgb = mix(vec3(1.), vec3(1.,.1,.1), abs(sin(p.x*2.)));
        rgb *= 1.-sat((abs(p.y-4.)-1.)*10.);
        emit = 2.*sat(sin(p.x*freq+t)*sin(p.y*freq)*sin(p.z*freq-t))*rgb;
    }
    col += emit;
    return col;
}

vec3 rdr(vec2 uv)
{
    uv += (vec2(rand(), rand())-.5)*.02*pow(FFT(.8)*2.,2.)*sat(sin(iTime*.33));
    float stp = 1.9;
    float t = floor(iTime/stp)*stp;
    uv *= r2d((hash11(t)-.5)*.5);
    vec3 col = vec3(0.);

    vec3 ro = vec3(sat(sin(iTime)*3.)*3.,3.5+mod(iTime, 2.)*sign(sin(iTime*.33)),-4.);
    vec3 ta = vec3(2.,3.,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv);
    vec3 res = trace(ro, rd, 128);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = getMat(p, n, rd, res);
        if (true)
        {
            vec3 refl = rd;
            for (float i = 0.; i < 8.; ++i)
            {
                refl = reflect(refl, n);
                float gloss = pow(texture(iChannel2, (p.xz+p.y*.5)*.5).x, 2.);
                refl = normalize(refl+normalize(vec3(rand(),rand(), rand())-.5)*.2*gloss);
                vec3 resrefl = trace(p+n*.01, refl, 128);
                if (resrefl.y > 0.)
                {
                    p = p+refl*resrefl.y;
                    n = getNorm(p, resrefl.x);
                    col += .5*getMat(p, n, refl, resrefl);
                }
            }

        }
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    _seed = iTime+texture(iChannel0, uv).x;
    vec3 col = rdr(uv);

    vec2 off = vec2(1., -1.)/(iResolution.x*1.5);

    if (true) // Not so cheap antialiasing
    {
        //col = vec3(1.,0.,0.);
        vec3 acc = col;
        acc += rdr(uv+off.xx);
        acc += rdr(uv+off.xy);
        acc += rdr(uv+off.yy);
        acc += rdr(uv+off.yx);
        col = acc/5.;

    }
    col *= 1.9/(col+1.);

    col = sat(col);
    col = mix(col, texture(iChannel1, fragCoord/iResolution.xy).xyz, .3);
    fragColor = vec4(col,1.0);
}

