//Rendering a Sphere

struct Camera 
{
    vec3 position;
    vec3 direction;
    float zoom; 
} camera;

struct Sphere
{
    vec3 position;
    float radius;     
} sphere;

void setupScene()
{
    camera.position = vec3(0., 0., 3.5);
    camera.direction = vec3(0., 0., -1.);
    camera.zoom = 1.0;

    sphere.position = vec3(0., 0., 0.);
    sphere.radius = 0.3;
}

bool solveQuadratic(float a, float b, float c, out float t0, out float t1)
{
    float disc = b * b - 4. * a * c;

    if (disc < 0.)
    {
        return false;
    } 

    if (disc == 0.)
    {
        t0 = t1 = -b / (2. * a);
        return true;
    }

    t0 = (-b + sqrt(disc)) / (2. * a);
    t1 = (-b - sqrt(disc)) / (2. * a);
    return true;    
}

bool intersect(vec3 direction, out float refl)
{
    vec3 L = camera.position - sphere.position;

    float a = dot(direction, direction);
    float b = 2. * dot(direction, L);
    float c = dot(L, L) - pow(sphere.radius, 2.);

    float t0;
    float t1;

    if (solveQuadratic(a, b, c, t0, t1))
    {
        float t = t0;
        if (t1 < t0)
        {
            t = t1;
        }

        vec3 Phit = camera.position + t * direction;
        vec3 surfaceNormal = normalize(Phit - sphere.position);

        refl = -dot(direction, surfaceNormal);

        return true;
    }  

    return false;
}

vec3 rayTrace(vec3 direction)
{
    float refl = 1.;
    if (intersect(direction, refl))
    {
        return refl * vec3(0.9, 0.2, 0.3);
    }

    return vec3(0., 0., 0.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{        
    setupScene();

    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= (iResolution.x / iResolution.y); 

    vec3 direction = normalize(vec3(uv, camera.zoom) - camera.position);

    sphere.position.y = sin(iTime * 6.) / 3.;

    vec3 col = rayTrace(direction);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
