struct Point {
    float x;
    float y;
};
vec3 circle(float, float, Point, float);
bool isOnCircle(float, float, Point, float);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Puede coincidir con el primer circulo o con el segundo
    vec3 circleColor = circle(fragCoord.x, fragCoord.y, Point(400., 225.), 5.);
    vec3 circleColor2 = circle(fragCoord.x, fragCoord.y, Point(700., 105.), 10.);
    vec3 circleColor3 = circle(fragCoord.x, fragCoord.y, Point(10., 270.), 5.);

    vec3 final = circleColor + circleColor2 + circleColor3;

    // Output to screen
    fragColor = vec4(final,1.0);
}

vec3 circle(float x, float y, Point centerPoint, float radius) {
    if(isOnCircle(x, y, centerPoint, radius)) {
        return vec3(1,1,1);
    } else {
        return vec3(0,0,0);
    }
}

bool isOnCircle(float x, float y, Point centerPoint, float radius) {
    //When is 2, is a perfect circle
    float expOrShape = 1.7-cos(iTime);

    //Formula of circle
    if(sqrt(
           pow(abs(x-centerPoint.x), expOrShape) + 
           pow(abs(y-centerPoint.y), expOrShape)  
           ) < radius
      ) {
        return true;
    } else {
        return false;
    }
}
