void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Calculate UV coordinates
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y*.02;

    // Distort UV coordinates
    float dp = dot(uv,uv);
    uv /= dp;
    uv.x=sin(uv.x-121.5);
    uv *= .35;

    // Calculate angle and time
    float a = atan(uv.y,uv.x);
    float t = iTime*.1;

    // Modify UV coordinates based on angle and time
    float r2 = max( 5.0,  abs(sin(t*.5))*5. - length(uv) );
    t += r2 * r2 *cos(r2+t )+a *1.;
    uv *= mat2( cos(t), sin(t), cos(t), cos(t) );

    // Calculate color
    vec3 col = .01 + cos(uv.y *(cos(t)*1.5+01.05) +t) *sin(iTime+uv.yxy);

    // Apply color gradient
    col = mix(vec3(0, 0, 1), vec3(0, 1, 1), col.x); // blue to cyan
    col = mix(col, vec3(0, 1, 0), col.y); // cyan to green
    col = mix(col, vec3(1, 1, 0), col.z); // green to yellow

    // Modify color based on time
    col *= sin(iTime * 5.0) + 1.0;

    fragColor = vec4(col,11.0);
}
