//Based on https://thebookofshaders.com/edit.php#11/wood.frag
//scale the painting
#define paintScale 1.
#define timeScale 1.

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

float lines(in vec2 pos, float b){
    float scale = 10.0;
    pos *= scale;
    return smoothstep(0.0,.5+b*.5,
                    abs((sin(pos.x*1.)+b*2.0))*0.95);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates <-0.5, 0.5>
    vec2 uv = fragCoord / iResolution.xy;
    uv *= iResolution.x / iResolution.y;
    float t = (1.-iTime/15.)*timeScale;
    uv = (uv + noise(uv*2.))*rotate2d(t);
    uv *= paintScale;
    float pattern = lines(uv,0.5);
    //pattern = 1.; //makes it look better when scaled
    vec3 col = vec3(0.0);
    col += step(0.15, noise(uv*10.));
    col -= step(0.2, noise(uv*10.));
    // Output to screen
    fragColor = vec4(col, 1.0);
}
