#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
float sdCutDisk( in vec2 p, in float r, in float h )
{
    float w = sqrt(r*r-h*h); // constant for any given shape
    p.x = abs(p.x);
    float s = max( (h-r)*p.x*p.x+w*w*(h+r-2.0*p.y), h*p.x-w*p.y );
    return (s<0.0) ? length(p)-r :
           (p.x<w) ? h - p.y     :
                     length(p-vec2(w,h));
}
float sdArc( in vec2 p, in vec2 sc, in float ra, float rb )
{
    // sc is the sin/cos of the arc's aperture
    p.x = abs(p.x);
    return ((sc.y*p.x>sc.x*p.y) ? length(p-sc*ra) : 
                                  abs(length(p)-ra)) - rb;
}
float s(vec2 p,float e,float s){
    p.y*=1./e;
    return length(p)-s;
}
float Lx(vec2 p,float l,float s){
    p.x-=clamp(p.x,-l,l);
    return length(p)-s;
}
float Ly(vec2 p,float l,float s){
    p.y-=clamp(p.y,-l,l);
    return length(p)-s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x*=iResolution.x/iResolution.y;
    vec3 col=vec3(0.);
    vec3 bl=vec3(0.,0.,1.);
    float l=0.01;
    float f=step(abs(sdCutDisk(uv,0.8,-0.6)),l);
    col=mix(col,bl,f);
    f=step(abs(sdCutDisk(uv-vec2(0.,-0.2),0.65,-0.4)),l);
    col=mix(col,bl,f);
    f=step(s(uv-vec2(0.2,0.36),1.5,0.2),0.);
    col=mix(col,vec3(1.),f);
    f=step(s(uv-vec2(-0.2,0.36),1.5,0.2),0.);
    col=mix(col,vec3(1.),f);
    f=step(abs(s(uv-vec2(0.1,0.36),1.5,0.05)),0.02);
    col=mix(col,vec3(0.),f);
    f=step(abs(s(uv-vec2(-0.1,0.36),1.5,0.05)),0.02);
    col=mix(col,vec3(0.),f);
    f=step(Ly(uv-vec2(0.,-0.13),0.25,l),0.);
    col=mix(col,bl,f);
    f=step(sdArc(uv*vec2(0.7,1.3)-vec2(0.,-0.2),vec2(-0.8,0.01),0.3,l),0.);
    col=mix(col,bl,f);
    f=step(s(uv-vec2(0.,0.15),1.,0.08),0.);
    col=mix(col,vec3(1.,0.,0.),f);
    f=step(s(uv-vec2(-0.03,0.15),1.5,0.02),0.);
    col=mix(col,vec3(1.),f);
    f=step(Lx(uv-vec2(0.5,-0.1),0.2,l),0.);
    col=mix(col,bl,f);
    f=step(Lx(uv-vec2(-0.5,-0.1),0.2,l),0.);
    col=mix(col,bl,f);
    f=step(Lx(uv*rot(0.3)-vec2(-0.48,-0.1),0.2,l),0.);
    col=mix(col,bl,f);
    f=step(Lx(uv*rot(-0.3)-vec2(-0.55,-0.1),0.2,l),0.);
    col=mix(col,bl,f);
    f=step(Lx(uv*rot(0.3)-vec2(0.55,-0.1),0.2,l),0.);
    col=mix(col,bl,f);
    f=step(Lx(uv*rot(-0.3)-vec2(0.48,-0.1),0.2,l),0.);
    col=mix(col,bl,f);
    f=step(Lx(uv-vec2(0.,-0.62),0.5,0.02),0.);
    col=mix(col,vec3(1.,0.,0.),f);
    f=step(s(uv-vec2(0.,-0.68),1.,0.07),0.);
    col=mix(col,vec3(1.,1.,0.),f);
    f=step(Lx(uv-vec2(0.,-0.68),0.08,0.01),0.);
    col=mix(col,vec3(0.85,0.85,0.),f);
    f=step(Ly(uv-vec2(0.,-0.7),0.02,0.005),0.);
    col=mix(col,vec3(0.85,0.85,0.),f);
    f=step(s(uv-vec2(0.,-0.72),1.,0.013),0.);
    col=mix(col,vec3(0.85,0.85,0.),f);
    fragColor = vec4(col,1.0);
}
