#define tex0(pos) texture(iChannel0, pos)
#define tex1(pos) texture(iChannel1, pos)
#define tex2(pos) texture(iChannel2, pos)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = tex0(uv);
}

#define tex0(pos) texture(iChannel0, pos)
#define tex1(pos) texture(iChannel1, pos)
#define tex2(pos) texture(iChannel2, pos)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 p = vec3(vec2(1.)/iResolution.xy, 0.);
    float samp = tex0(vec2(uv.x - p.x, uv.y - p.y)).x;
    float col = tex0(uv).x;
    if (fragCoord.x <= 1. && fragCoord.y <= 1. || samp >=1.) {
        col += -1.;
        col = abs( col);
    }

    fragColor = vec4(col);
}
