void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float TAU = 6.283185307179586;
    vec2 uv = fragCoord / iResolution.xy;

    //uv.x // horizontal
    uv.y // vertical
    += iTime * .25;

    float offset = (1. + sin(uv.x * TAU)) * .5;

    float threshold = .5;

    float t = smoothstep(
        threshold,
        threshold * .95,
        (1. + cos((uv.y + offset) * TAU)) * .5
    );

    vec3 color = mix(
        vec3(1., 1., 0.),
        vec3(0., 1., 1.),
        t
    );

    fragColor = vec4(color, 1.);
}
